// --- MODEL CLASS for Chapters ---
class Chapter {
  final int id;
  final int number;
  final String title;
  final String titleSanskrit;
  final String summary;
  final int verses;
  final int completedPercentage;

  Chapter({
    required this.id,
    required this.number,
    required this.title,
    required this.titleSanskrit,
    required this.summary,
    required this.verses,
    this.completedPercentage = 0,
  });

  factory Chapter.fromMap(Map<String, dynamic> map) {
    return Chapter(
      id: map['id'] as int,
      number: map['chapter_number'] as int,
      title: map['chapter_name'] as String,
      titleSanskrit: map['chapter_name_sanskrit'] as String,
      summary: map['summary'] as String,
      verses: map['total_verses'] as int,
      completedPercentage: map['completed_percentage'] as int? ?? 0,
    );
  }
}

// üÜï NEW MODEL CLASS for Characters üÜï
class Character {
  final int id;
  final String name;
  final String imagePath; // Path to local asset image
  final String summary;
  final String role; // Role in Gita or Mahabharat
  final String keyLessons;
  final String quote; // Famous quote from Gita
  final String quoteChapter; // Which chapter the quote is from

  Character({
    required this.id,
    required this.name,
    required this.imagePath,
    required this.summary,
    required this.role,
    required this.keyLessons,
    required this.quote,
    required this.quoteChapter,
  });

  factory Character.fromMap(Map<String, dynamic> map) {
    return Character(
      id: map['id'] as int,
      name: map['name'] as String,
      imagePath: map['image_path'] as String,
      summary: map['summary'] as String,
      role: map['role'] as String,
      keyLessons: map['key_lessons'] as String,
      quote: map['quote'] as String,
      quoteChapter: map['quote_chapter'] as String,
    );
  }
}

// --- MAIN DATABASE HELPER CLASS ---
class DatabaseHelper {
  static final DatabaseHelper instance = DatabaseHelper._init();
  static Database? _database;

  DatabaseHelper._init();

  Future<Database> get database async {
    if (_database != null) return _database!;
    _database = await _initDB('bhagavad_gita.db');
    return _database!;
  }

  // Initialize database
  Future<Database> _initDB(String filePath) async {
    final dbPath = await getDatabasesPath();
    final path = join(dbPath, filePath);

    // // Delete database during development to reset
    // await deleteDatabase(path);

    return await openDatabase(path, version: 4, onCreate: _createDB);
  }

  // --- CREATE ALL TABLES ---
  Future _createDB(Database db, int version) async {
    // 1Ô∏è‚É£ Chapters meta table
    await db.execute('''
      CREATE TABLE chapters (
        id INTEGER PRIMARY KEY AUTOINCREMENT,
        chapter_number INTEGER NOT NULL,
        chapter_name TEXT NOT NULL,
        chapter_name_sanskrit TEXT,
        summary TEXT,
        total_verses INTEGER NOT NULL,
        completed_percentage INTEGER DEFAULT 0
      );
    ''');

    await _createBookmarksTable(db); 
    // Insert metadata for all 18 chapters

    await _createCharactersTable(db);
    await _insertInitialCharacters(db);

    await _insertInitialChapters(db);

