  // --- FETCH ALL CHAPTERS ---
  Future<List<Chapter>> fetchAllChapters() async {
    final db = await instance.database;
    final maps = await db.query('chapters', orderBy: 'chapter_number ASC');
    return List.generate(maps.length, (i) => Chapter.fromMap(maps[i]));
  }

